INSERT INTO `tt_newproject_xmldata_elements` (`id`,`id_parent`,`id_doc`,`pos`,`name`,`value`,`type`) VALUES ('11478','12844','0','0',NULL,'<!--\n	NAVIGATION\n-->\n<xsl:template name=\"navigation\">\n	<div id=\"navigation\">\n		<h2 class=\"invisible\">Navigation</h2>\n		<ul>\n		<xsl:for-each select=\"document(\'get:navigation\')/proj:pages_struct/*[not(@nav_hidden=\'true\')]\">\n			<xsl:choose>\n				<xsl:when test=\"local-name() = \'separator\'\">\n					<xsl:text disable-output-escaping=\"yes\">&lt;/ul&gt;&lt;ul&gt;</xsl:text>\n				</xsl:when>\n				<xsl:otherwise>\n					<xsl:call-template name=\"element_navigation_l1\" />\n				</xsl:otherwise>\n			</xsl:choose>\n		</xsl:for-each>\n		</ul>\n	</div>\n</xsl:template>\n\n<!-- ELEMENT NAVIGATION -->\n<xsl:template name=\"element_navigation_l1\">\n	<li>\n	<xsl:choose>\n		<xsl:when test=\"@status = \'active\' or @status = \'parent-of-active\'\" >\n			<xsl:call-template name=\"edit:a\">\n				<!--xsl:with-param name=\"content\"><xsl:value-of select=\"@name\" /></xsl:with-param-->\n				<xsl:with-param name=\"href_id\"><xsl:value-of select=\"@db:id\" /></xsl:with-param>\n				<xsl:with-param name=\"class\"><xsl:value-of select=\"translate(@status,\'-\',\'_\')\" /></xsl:with-param>\n			</xsl:call-template>\n			<xsl:if test=\"count(./*[not(@nav_hidden=\'true\')]) > 0\">\n				<xsl:text disable-output-escaping=\"yes\">&lt;div id=\"subnav\"&gt;&lt;ul&gt;</xsl:text>\n			</xsl:if>\n			<xsl:for-each select=\"./*[not(@nav_hidden=\'true\')]\">\n				<xsl:if test=\"../@status = \'parent-of-active\' or ../@status = \'active\'\">\n					<xsl:choose>\n						<xsl:when test=\"local-name() = \'separator\'\">\n							<xsl:text disable-output-escaping=\"yes\">&lt;/ul&gt;&lt;ul&gt;</xsl:text>\n						</xsl:when>\n						<xsl:otherwise>\n							<xsl:call-template name=\"element_navigation_l2\" />\n						</xsl:otherwise>\n					</xsl:choose>\n				</xsl:if>\n			</xsl:for-each>\n			<xsl:if test=\"count(./*[not(@nav_hidden=\'true\')]) > 0\">\n				<xsl:text d