overflow: hidden;\n    position: relative;\n    clear: both;\n    margin: 0;\n    padding-top: 0.2em;\n    padding-bottom: 0.2em;\n    padding-left: 0.5em;\n    font-size: 0.88em;\n}\n#content .calendar li p {\n    display: inline;\n}\n#content .calendar li h2 {\n    line-height: 1.5em;\n}\n#content .calendar li div.misc, #content .calendar li div.education, #content .calendar li div.event {\n    position: relative;\n    background-color: #ffffff;\n    margin-bottom: 0em;\n    padding-top: 0.2em !important;\n    padding-top: 0.4em;\n    padding-left: 0.4em;\n}\n#content .calendar li div.event {\n    margin-left: 4em;\n}\n#content .calendar li div.education {\n    margin-left: 17.6em;\n}\n#content .calendar li div.misc {\n    margin-left: 32em;\n}\n#content .calendar span.category {\n    font-size: 0.85em;\n}\n#content .calendar span.day, .calendar span.dayofweek {\n    position: absolute;\n    display: block;\n    text-align: right;\n    font-weight: bold;\n    width: 1.8em;\n    top: 0em;\n}\n#content .calendar span.day {\n    right: 2.6em;\n}\n#content .calendar span.dayofweek {\n    right: 0.6em;\n}\n#content .calendar .exhibits {\n    position: absolute;\n    top: 0;\n    left: 0;\n    padding: 0;\n    margin: 0;\n}\n#content .calendar div.exhib1, #content .calendar div.exhib2, #content .calendar div.exhib3 {\n    position: absolute;\n    top: 0;\n    padding-bottom: 40em;\n    margin-bottom: -40em;\n    width: 1.3em;\n    background-color: #dee7ec;\n}\n#content .calendar div.exhib1 {\n    left: 0;\n}\n#content .calendar div.exhib2 {\n    left: 1.3em;\n}\n#content .calendar div.exhib3 {\n    left: 2.6em;\n}\n/* }}} */\n\n	<xsl:for-each select=\"document(\'get:colors\')//proj:colorscheme[@name]\">\n/* {{{ color <xsl:value-of select=\"@name\"/> */\n.color<xsl:value-of select=\"@name\"/>, #content .color<xsl:value-of select=\"@name\"/> a {\n    background-color: <xsl:value-of select=\"color[@name = \'bg\']/@value\"/> !important;\n    color: <xsl:value-of select=\"c